import React, { useState, useEffect } from 'react';
import { getSimpleFixtures, type SimpleFixture } from '../services/supabase-simple';

const HomePage: React.FC = () => {
  const [fixtures, setFixtures] = useState<SimpleFixture[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadFixtures();
  }, []);

  const loadFixtures = async () => {
    try {
      setLoading(true);
      setError(null);

      // Load upcoming fixtures for Premier League (competition ID 1)
      const now = new Date();
      const nowIso = now.toISOString();

      const fixturesData = await getFixtures({
        competitionId: 1, // Premier League
        dateFrom: nowIso,
        order: 'asc',
        limit: 20
      });

      setFixtures(fixturesData);
    } catch (err) {
      console.error('Failed to load fixtures:', err);
      setError('Failed to load fixtures. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="home-page">
        <header>
          <h1>Premier League TV Schedule UK</h1>
          <p>Find which broadcaster shows every match: Sky Sports, TNT Sports, BBC</p>
        </header>

        <main>
          <div className="wrap">
            <div className="loading">Loading fixtures...</div>
          </div>
        </main>
      </div>
    );
  }

  if (error) {
    return (
      <div className="home-page">
        <header>
          <h1>Premier League TV Schedule UK</h1>
          <p>Find which broadcaster shows every match: Sky Sports, TNT Sports, BBC</p>
        </header>

        <main>
          <div className="wrap">
            <div className="error">{error}</div>
            <button onClick={loadFixtures}>Retry</button>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="home-page">
      <header>
        <h1>Premier League TV Schedule UK</h1>
        <p>Find which broadcaster shows every match: Sky Sports, TNT Sports, BBC</p>
      </header>

      <main>
        <div className="wrap">
          {fixtures.length === 0 ? (
            <div className="no-fixtures">
              <p>No upcoming fixtures found.</p>
            </div>
          ) : (
            <div className="fixtures-list">
              <h2>Upcoming Fixtures</h2>
              {fixtures.map(fixture => (
                <FixtureCard key={fixture.id} fixture={fixture} />
              ))}
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default HomePage;
