import React from 'react';

interface HeaderProps {
  title?: string;
  subtitle?: string;
}

const Header: React.FC<HeaderProps> = ({ 
  title = "Premier League TV Schedule UK",
  subtitle = "Find which broadcaster shows every match: Sky Sports, TNT Sports, BBC"
}) => {
  return (
    <header style={{
      background: 'white',
      borderBottom: '1px solid #e5e7eb',
      padding: '16px 24px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      flexWrap: 'wrap',
      gap: '16px'
    }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
        <a 
          href="/" 
          style={{ 
            display: 'flex', 
            alignItems: 'center', 
            textDecoration: 'none',
            color: 'inherit'
          }}
        >
          <img 
            src="/logo.svg" 
            alt="fixtures.app" 
            style={{ height: '32px', width: 'auto' }}
          />
        </a>
        <div>
          <h1 style={{ 
            margin: 0, 
            fontSize: '20px',
            fontWeight: '600',
            color: '#1f2937'
          }}>
            {title}
          </h1>
          <p style={{ 
            margin: 0, 
            fontSize: '14px',
            color: '#6b7280'
          }}>
            {subtitle}
          </p>
        </div>
      </div>

      <nav style={{ 
        display: 'flex', 
        gap: '20px',
        alignItems: 'center'
      }}>
        <a 
          href="/" 
          style={{ 
            color: '#6366f1', 
            textDecoration: 'none', 
            fontSize: '15px',
            fontWeight: '500',
            padding: '8px 12px',
            borderRadius: '6px',
            transition: 'background-color 0.2s'
          }}
          onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#f8fafc'}
          onMouseOut={(e) => e.currentTarget.style.backgroundColor = 'transparent'}
        >
          Home
        </a>
        <a 
          href="/fixtures" 
          style={{ 
            color: '#6366f1', 
            textDecoration: 'none', 
            fontSize: '15px',
            fontWeight: '500',
            padding: '8px 12px',
            borderRadius: '6px',
            transition: 'background-color 0.2s'
          }}
          onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#f8fafc'}
          onMouseOut={(e) => e.currentTarget.style.backgroundColor = 'transparent'}
        >
          Fixtures
        </a>
        <a 
          href="/clubs" 
          style={{ 
            color: '#6366f1', 
            textDecoration: 'none', 
            fontSize: '15px',
            fontWeight: '500',
            padding: '8px 12px',
            borderRadius: '6px',
            transition: 'background-color 0.2s'
          }}
          onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#f8fafc'}
          onMouseOut={(e) => e.currentTarget.style.backgroundColor = 'transparent'}
        >
          Clubs
        </a>
        <a 
          href="/about" 
          style={{ 
            color: '#6366f1', 
            textDecoration: 'none', 
            fontSize: '15px',
            fontWeight: '500',
            padding: '8px 12px',
            borderRadius: '6px',
            transition: 'background-color 0.2s'
          }}
          onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#f8fafc'}
          onMouseOut={(e) => e.currentTarget.style.backgroundColor = 'transparent'}
        >
          About
        </a>
        <a 
          href="/admin" 
          style={{ 
            color: '#64748b', 
            textDecoration: 'none', 
            fontSize: '14px',
            fontWeight: '400',
            padding: '6px 10px',
            borderRadius: '4px',
            border: '1px solid #e2e8f0',
            transition: 'all 0.2s'
          }}
          onMouseOver={(e) => {
            e.currentTarget.style.backgroundColor = '#f1f5f9';
            e.currentTarget.style.borderColor = '#cbd5e1';
          }}
          onMouseOut={(e) => {
            e.currentTarget.style.backgroundColor = 'transparent';
            e.currentTarget.style.borderColor = '#e2e8f0';
          }}
        >
          Admin
        </a>
      </nav>
    </header>
  );
};

export default Header;
